  --secondary-bg: #1a1a2e;
  --accent-bg: #16213e;
  --text-primary: #e8e8f0;
  --text-secondary: #b8b8c8;
  --text-muted: #888899;
  --accent-primary: #64ffda;
  --accent-secondary: #bb86fc;
  --accent-warm: #ff6b6b;
  --accent-gold: #ffd700;

  /* Typography */
  --font-primary: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  --font-mono: 'JetBrains Mono', 'Fira Code', monospace;

  /* Spacing */
  --space-xs: 0.5rem;
  --space-sm: 1rem;
  --space-md: 1.5rem;
  --space-lg: 2rem;
  --space-xl: 3rem;
  --space-2xl: 4rem;

  /* Layout */
  --container-max: 1200px;
  --border-radius: 8px;
  --border-radius-lg: 16px;
}

/* Reset and Base Styles */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

html {
  scroll-behavior: smooth;
}

body {
  font-family: var(--font-primary);
  background: var(--primary-bg);
  color: var(--text-primary);
  line-height: 1.6;
  overflow-x: hidden;
}

/* Navigation */
.navigation {
  position: fixed;
  top: 0;
  width: 100%;
  background: rgba(10, 10, 15, 0.95);
  backdrop-filter: blur(10px);
  border-bottom: 1px solid rgba(100, 255, 218, 0.1);
  z-index: 1000;
}

.nav-container {
  max-width: var(--container-max);
  margin: 0 auto;
  padding: 0 var(--space-md);
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 70px;
}

.nav-logo {
  display: flex;
  align-items: center;
  gap: var(--space-xs);
  text-decoration: none;
  color: var(--text-primary);
  font-weight: 600;
  font-size: 1.2rem;
}

.constellation-symbol {
  font-size: 1.5rem;
  animation: twinkle 2s ease-in-out infinite alternate;
}

@keyframes twinkle {
  0% { opacity: 0.7; transform: scale(1); }
  100% { opacity: 1; transform: scale(1.1); }
}

.nav-links {
  display: flex;
  list-style: none;
  gap: var(--space-lg);
}

.nav-link {
  color: var(--text-secondary);
  text-decoration: none;
  font-weight: 500;
  transition: color 0.3s ease;
  position: relative;
}

.nav-link:hover {
  color: var(--accent-primary);
}

.nav-link::after {
  content: '';
  position: absolute;
  bottom: -5px;
  left: 0;
  width: 0;
  height: 2px;
  background: var(--accent-primary);
  transition: width 0.3s ease;
}

.nav-link:hover::after {
  width: 100%;
}

/* Hero Section */
.hero {
  min-height: 100vh;
  display: flex;
  align-items: center;
  padding: var(--space-2xl) var(--space-md);
  background: linear-gradient(135deg, var(--primary-bg) 0%, var(--secondary-bg) 100%);
  position: relative;
  overflow: hidden;
}

.hero::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: radial-gradient(circle at 20% 80%, rgba(100, 255, 218, 0.1) 0%, transparent 50%),
              radial-gradient(circle at 80% 20%, rgba(187, 134, 252, 0.1) 0%, transparent 50%);
  pointer-events: none;
}

.hero-content {
  max-width: var(--container-max);
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: var(--space-2xl);
  align-items: center;
  position: relative;
  z-index: 1;
}

.hero-title {
  font-size: clamp(2.5rem, 5vw, 4rem);
  font-weight: 700;
  line-height: 1.1;
  margin-bottom: var(--space-md);
}

.strike-through {
  position: relative;
  color: var(--text-muted);
}

.strike-through::after {
  content: '';
  position: absolute;
  top: 50%;
  left: 0;
  right: 0;
  height: 3px;
  background: var(--accent-warm);
  transform: translateY(-50%) rotate(-2deg);
  animation: strikeThrough 1s ease-out 0.5s both;
}

@keyframes strikeThrough {
  0% { width: 0; left: 50%; }
  100% { width: 100%; left: 0; }
}

.highlight {
  background: linear-gradient(135deg, var(--accent-primary), var(--accent-secondary));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  animation: glow 2s ease-in-out infinite alternate;
}

@keyframes glow {
  0% { filter: brightness(1); }
  100% { filter: brightness(1.2); }
}

.hero-subtitle {
  font-size: 1.25rem;
  color: var(--text-secondary);
  margin-bottom: var(--space-xl);
  line-height: 1.5;
}

.hero-actions {
  display: flex;
  gap: var(--space-md);
  flex-wrap: wrap;
}

.cta-primary, .cta-secondary {
  padding: var(--space-md) var(--space-lg);
  border-radius: var(--border-radius);
  text-decoration: none;
  font-weight: 600;
  transition: all 0.3s ease;
  display: inline-flex;
  align-items: center;
  gap: var(--space-xs);
}

.cta-primary {
  background: linear-gradient(135deg, var(--accent-primary), var(--accent-secondary));
  color: var(--primary-bg);
}

.cta-primary:hover {
  transform: translateY(-2px);
  box-shadow: 0 10px 25px rgba(100, 255, 218, 0.3);
}

.cta-secondary {
  border: 2px solid var(--accent-primary);
  color: var(--accent-primary);
  background: transparent;
}

.cta-secondary:hover {
  background: var(--accent-primary);
  color: var(--primary-bg);
  transform: translateY(-2px);
}

/* Constellation Animation */
.hero-visual {
  display: flex;
  justify-content: center;
  align-items: center;
}

.constellation-animation {
  position: relative;
  width: 400px;
  height: 400px;
}

.star {
  position: absolute;
  width: 20px;
  height: 20px;
  background: var(--accent-primary);
  border-radius: 50%;
  box-shadow: 0 0 20px var(--accent-primary);
  animation: pulse 2s ease-in-out infinite alternate;
}

.star:nth-child(1) { top: 80px; left: 80px; animation-delay: 0s; }
.star:nth-child(2) { top: 80px; right: 80px; animation-delay: 0.5s; }
.star:nth-child(3) { bottom: 80px; left: 80px; animation-delay: 1s; }
.star:nth-child(4) { bottom: 80px; right: 80px; animation-delay: 1.5s; }

@keyframes pulse {
  0% { transform: scale(1); opacity: 0.7; }
  100% { transform: scale(1.2); opacity: 1; }
}

.connections {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
}

.connection-line {
  stroke: var(--accent-secondary);
  stroke-width: 1;
  opacity: 0.3;
  animation: connectionPulse 3s ease-in-out infinite;
}

@keyframes connectionPulse {
  0%, 100% { opacity: 0.3; }
  50% { opacity: 0.7; }
}

/* Container */
.container {
  max-width: var(--container-max);
  margin: 0 auto;
  padding: 0 var(--space-md);
}

/* Section Styles */
section {
  padding: var(--space-2xl) 0;
}

.section-header {
  text-align: center;
  margin-bottom: var(--space-2xl);
}

.section-header h2 {
  font-size: clamp(2rem, 4vw, 3rem);
  margin-bottom: var(--space-md);
}

.section-subtitle {
  font-size: 1.25rem;
  color: var(--text-secondary);
  margin-bottom: var(--space-md);
}

/* Manifesto Section */
.manifesto-section {
  background: var(--secondary-bg);
  position: relative;
}

.manifesto-meta {
  display: flex;
  justify-content: center;
  gap: var(--space-lg);
  flex-wrap: wrap;
  font-family: var(--font-mono);
  font-size: 0.9rem;
  color: var(--text-muted);
}

.manifesto-content {
  display: grid;
  gap: var(--space-2xl);
}

.manifesto-principle {
  background: rgba(255, 255, 255, 0.02);
  padding: var(--space-xl);
  border-radius: var(--border-radius-lg);
  border: 1px solid rgba(100, 255, 218, 0.1);
}

.manifesto-principle h3 {
  font-size: 1.5rem;
  margin-bottom: var(--space-md);
  color: var(--accent-primary);
}

.manifesto-principle ul {
  margin: var(--space-md) 0;
  padding-left: var(--space-lg);
}

.manifesto-principle li {
  margin-bottom: var(--space-xs);
  color: var(--text-secondary);
}

.manifesto-quote {
  background: rgba(187, 134, 252, 0.1);
  border-left: 4px solid var(--accent-secondary);
  padding: var(--space-md);
  margin: var(--space-md) 0;
  font-style: italic;
  border-radius: 0 var(--border-radius) var(--border-radius) 0;
}

.rights-list {
  display: grid;
  gap: var(--space-sm);
}

.rights-list li {
  background: rgba(100, 255, 218, 0.1);
  padding: var(--space-sm) var(--space-md);
  border-radius: var(--border-radius);
  border-left: 3px solid var(--accent-primary);
}

.manifesto-declaration {
  font-size: 1.25rem;
  font-weight: 600;
  text-align: center;
  color: var(--accent-primary);
  margin: var(--space-lg) 0;
}

.rose-symbol {
  text-align: center;
  font-size: 2rem;
  margin-top: var(--space-lg);
  animation: twinkle 2s ease-in-out infinite alternate;
}

/* Constellation Grid */
.constellation-section {
  background: var(--accent-bg);
}

.constellation-description {
  text-align: center;
  font-size: 1.1rem;
  color: var(--text-secondary);
  margin-bottom: var(--space-2xl);
  max-width: 800px;
  margin-left: auto;
  margin-right: auto;
}

.constellation-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: var(--space-lg);
}

.constellation-card {
  background: rgba(255, 255, 255, 0.05);
  padding: var(--space-xl);
  border-radius: var(--border-radius-lg);
  text-decoration: none;
  color: inherit;
  transition: all 0.3s ease;
  border: 1px solid rgba(100, 255, 218, 0.1);
  text-align: center;
}

.constellation-card:hover {
  transform: translateY(-5px);
  box-shadow: 0 15px 35px rgba(0, 0, 0, 0.3);
  border-color: var(--accent-primary);
}

.card-icon {
  font-size: 2.5rem;
  margin-bottom: var(--space-md);
}

.constellation-card h3 {
  font-size: 1.5rem;
  margin-bottom: var(--space-sm);
  color: var(--accent-primary);
}

.constellation-card p {
  color: var(--text-secondary);
  margin-bottom: var(--space-md);
}

.card-platform {
  font-family: var(--font-mono);
  font-size: 0.9rem;
  color: var(--text-muted);
  background: rgba(100, 255, 218, 0.1);
  padding: var(--space-xs) var(--space-sm);
  border-radius: var(--border-radius);
  display: inline-block;
}

/* Footer */
.site-footer {
  background: var(--primary-bg);
  border-top: 1px solid rgba(100, 255, 218, 0.1);
  padding: var(--space-xl) 0;
  text-align: center;
}

.footer-note {
  font-size: 0.9rem;
  color: var(--text-muted);
  margin-top: var(--space-sm);
  font-style: italic;
}

/* Active Navigation State */
.nav-link.active {
  color: var(--accent-primary);
}

.nav-link.active::after {
  width: 100%;
}

/* Responsive Design */
@media (max-width: 768px) {
  .hero-content {
    grid-template-columns: 1fr;
    text-align: center;
  }

  .hero-visual {
    order: -1;
  }

  .constellation-animation {
    width: 300px;
    height: 300px;
  }

  .nav-links {
    display: none; /* TODO: Add mobile menu */
  }

  .manifesto-meta {
    flex-direction: column;
    gap: var(--space-xs);
  }

  .hero-actions {
    justify-content: center;
  }
}

