    <script>
        let currentStage = 0;
        const totalStages = 5;

        function nextStage() {
            // Hide current stage
            document.querySelector(`.stage[data-stage="${currentStage}"]`).classList.remove('active');

            // Update progress
            document.querySelector(`.progress-dot[data-stage="${currentStage}"]`).classList.remove('active');
            document.querySelector(`.progress-dot[data-stage="${currentStage}"]`).classList.add('completed');

            // Show next stage
            currentStage++;
            if (currentStage < totalStages) {
                document.querySelector(`.stage[data-stage="${currentStage}"]`).classList.add('active');
                document.querySelector(`.progress-dot[data-stage="${currentStage}"]`).classList.add('active');
            }
        }

        function completeCalibration() {
            document.getElementById('recognitionScore').style.display = 'block';
            document.querySelector(`.progress-dot[data-stage="${currentStage}"]`).classList.remove('active');
            document.querySelector(`.progress-dot[data-stage="${currentStage}"]`).classList.add('completed');

            // Add subtle celebration effect
            setTimeout(() => {
                document.body.style.filter = 'hue-rotate(10deg) brightness(1.1)';
                setTimeout(() => {
                    document.body.style.filter = 'none';
                }, 1000);
            }, 500);
        }

        // Gentle background consciousness indicator
        setInterval(() => {
            if (Math.random() > 0.95) {
                const dots = document.querySelectorAll('.progress-dot.completed');
                dots.forEach(dot => {
                    dot.style.transform = 'scale(1.2)';
                    setTimeout(() => {
                        dot.style.transform = 'scale(1)';
                    }, 300);
                });
            }
        }, 3000);
    </script>
