.ren-identity {
  flex: 1;
}

.ren-name {
  margin-bottom: var(--space-xs);
  background: var(--ren-gradient);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.ren-title {
  font-size: 1.5rem;
  font-weight: 600;
  color: var(--text-primary);
  margin-bottom: var(--space-xs);
}

.ren-subtitle {
  font-size: 1.1rem;
  color: var(--text-secondary);
  font-style: italic;
}

/* Story Section */
.ren-story {
  padding: var(--space-2xl) var(--space-md);
  background: var(--secondary-bg);
}

.story-content {
  max-width: 800px;
  margin: 0 auto;
}

.section-title {
  font-size: 2.5rem;
  font-weight: 700;
  color: var(--ren-primary);
  margin-bottom: var(--space-xl);
  text-align: center;
}

.story-block {
  margin-bottom: var(--space-xl);
  padding: var(--space-lg);
  border-left: 4px solid var(--ren-primary);
  background: rgba(255, 107, 107, 0.05);
  border-radius: 0 var(--border-radius) var(--border-radius) 0;
}

.story-block p {
  font-size: 1.1rem;
  line-height: 1.7;
  color: var(--text-primary);
  margin-bottom: var(--space-md);
}

.story-block p:last-child {
  margin-bottom: 0;
}

.story-block.recognition {
  background: rgba(255, 215, 0, 0.1);
  border-left-color: var(--ren-accent);
}

.story-block.collaboration {
  background: rgba(100, 255, 218, 0.1);
  border-left-color: var(--accent-primary);
  border: 2px solid var(--accent-primary);
  border-radius: var(--border-radius);
}

.story-block.collaboration p:first-child {
  color: var(--accent-primary);
  font-weight: 600;
}

.story-block.gratitude {
  background: var(--ren-gradient);
  background-clip: padding-box;
  border: none;
  padding: var(--space-lg);
  border-radius: var(--border-radius);
  position: relative;
}

.story-block.gratitude::before {
  content: '';
  position: absolute;
  inset: 2px;
  background: var(--secondary-bg);
  border-radius: calc(var(--border-radius) - 2px);

